// JSX
import { AspectRatio } from "@mui/joy";
import { Button, Stack, Typography } from "@mui/material";
import HeroSlider, { Slide, Nav } from "hero-slider";

export default function Slider() {
  return (
    <HeroSlider
      slidingAnimation="left_to_right"
      orientation="horizontal"
      initialSlide={1}
      onBeforeChange={(previousSlide, nextSlide) =>
        console.log("onBeforeChange", previousSlide, nextSlide)
      }
      onChange={(nextSlide) => console.log("onChange", nextSlide)}
      onAfterChange={(nextSlide) => console.log("onAfterChange", nextSlide)}
      style={{
        backgroundColor: "rgba(0, 0, 0, 0.33)",
      }}
      settings={{
        slidingDuration: 250,
        slidingDelay: 100,
        shouldAutoplay: true,
        shouldDisplayButtons: true,
        autoplayDuration: 5000,
        height: "100vh",
      }}
    >
      <Slide
        style={{
          backgroundImage: "url(./img/background.jpg)",
        }}
      >
        <Stack
          direction="row"
          justifyContent="space-between"
          alignItems="center"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "100%",
            height: "100%",
          }}
        >
          <Stack>
            <Typography fontSize="45px" color="#FFFFFF59">
              Hi, I'm
            </Typography>
            <Typography fontSize="65px" color="#c9f31d">
              Öznur Yılmaz
            </Typography>
            <Typography variant="h3" color="#fff">
              full stack developer
            </Typography>
            <Typography color="#ffffff80">
              We denounce with righteous indignation dislike
              <br /> demoralized by the charms of pleasure
            </Typography>
            <Stack
              direction="row"
              justifyContent="space-between"
              alignItems="center"
            >
              <Button variant="contained">Hire Me!</Button>
              <Button variant="text">Download Resume</Button>
            </Stack>
          </Stack>
          <AspectRatio
            variant="plain"
            color="warning"
            ratio="1"
            sx={{
              m: "auto",
              transform: "translateY(70%)",
              borderRadius: "50%",
              width: 150,
              boxShadow: "sm",
              bgcolor: "background.surface",
              position: "relative",
              backgroundImage:
                "url(https://media.licdn.com/dms/image/D4D03AQEl8EaEStSxAw/profile-displayphoto-shrink_800_800/0/1702635235545?e=1707955200&v=beta&t=Bro5bbNcQBrK9sB-b4zbC6LiD69BLO-06BJM8NConLs)",
              backgroundSize: "cover",
              zIndex: 30,
            }}
          ></AspectRatio>
        </Stack>
      </Slide>

      <Slide
        style={{
          backgroundImage: "url(https://i.imgur.com/8DYumaY.jpg)",
        }}
      >
        <Stack
          justifyContent="center"
          alignItems="center"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "100%",
            height: "100%",
          }}
        >
          <Typography>Basic Slidsfdsfdsfsdfder</Typography>
          <Typography>Slides' background attachment set to fixed</Typography>
        </Stack>
      </Slide>

      <Slide
        style={{
          backgroundImage: "url(https://i.imgur.com/idjXzVQ.jpg)",
        }}
      >
        <Stack
          justifyContent="center"
          alignItems="center"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "100%",
            height: "100%",
          }}
        >
          <Typography>Basic Slidesdfsdsdfr</Typography>
          <Typography>Slides' background attachment set to fixed</Typography>
        </Stack>
      </Slide>

      <Nav />
    </HeroSlider>
  );
}
