// JSX
import { Button, Stack, Typography } from "@mui/material";
import HeroSlider, { Slide, Nav } from "hero-slider";

export default function Slider() {
  return (
    <HeroSlider
      slidingAnimation="left_to_right"
      orientation="horizontal"
      initialSlide={1}
      onBeforeChange={(previousSlide, nextSlide) =>
        console.log("onBeforeChange", previousSlide, nextSlide)
      }
      onChange={(nextSlide) => console.log("onChange", nextSlide)}
      onAfterChange={(nextSlide) => console.log("onAfterChange", nextSlide)}
      style={{
        backgroundColor: "rgba(0, 0, 0, 0.33)",
      }}
      settings={{
        slidingDuration: 250,
        slidingDelay: 100,
        shouldAutoplay: true,
        shouldDisplayButtons: true,
        autoplayDuration: 5000,
        height: "100vh",
      }}
    >
      <Slide
        style={{
          backgroundImage: "url(./img/background.jpg)",
        }}
      >
        <Stack
          justifyContent="center"
          alignItems="center"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "100%",
            height: "100%",
          }}
        >
          <Stack>
            <Typography fontSize="45px" color="#FFFFFF59">
              Hi, I'm
            </Typography>
            <Typography fontSize="65px" color="#c9f31d">
              Öznur Yılmaz
            </Typography>
            <Typography variant="h3" color="#fff">
              full stack developer
            </Typography>
            <Typography>
              We denounce with righteous indignation dislike demoralized by the
              charms of pleasure
            </Typography>
            <Button variant="contained"></Button>
          </Stack>
        </Stack>
      </Slide>

      <Slide
        style={{
          backgroundImage: "url(https://i.imgur.com/8DYumaY.jpg)",
        }}
      >
        <Stack
          justifyContent="center"
          alignItems="center"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "100%",
            height: "100%",
          }}
        >
          <Typography>Basic Slidsfdsfdsfsdfder</Typography>
          <Typography>Slides' background attachment set to fixed</Typography>
        </Stack>
      </Slide>

      <Slide
        style={{
          backgroundImage: "url(https://i.imgur.com/idjXzVQ.jpg)",
        }}
      >
        <Stack
          justifyContent="center"
          alignItems="center"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            width: "100%",
            height: "100%",
          }}
        >
          <Typography>Basic Slidesdfsdsdfr</Typography>
          <Typography>Slides' background attachment set to fixed</Typography>
        </Stack>
      </Slide>

      <Nav />
    </HeroSlider>
  );
}
