import React, { useEffect, useState } from "react";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import { v4 as uuidv4 } from "uuid";
import {
  Avatar,
  Chip,
  Divider,
  Grid,
  IconButton,
  Stack,
  Tooltip,
  Typography,
} from "@mui/material";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import dateFormat from "dateformat";
import { Link } from "react-router-dom";

const Card = ({ children }) => {
  return <div className="card">{children}</div>;
};

const CodeBlock = () => {
  // eslint-disable-next-line no-unused-vars
  const [kanbanList, setKanbanList] = useState([]);
  const [toDo, setToDo] = useState([]);
  const [inProgress, setInProgress] = useState([]);
  const [done, setDone] = useState([]);
  const [isLoadingTodo, setIsLoadingTodo] = useState(false);
  const [isLoadingInProgress, setIsLoadingInProgress] = useState(false);
  const [isLoadingDone, setIsLoadingDone] = useState(false);
  const [data, setData] = useState(kanbanList);

  const GetToDoList = () => {
    axios
      .get(
        "http://localhost:5010/api/SupportRequest/GetApprovedListByStatusTypeLoggedEmployeeId?SupportRequestStatusType=0&PageRequest.Page=0&PageRequest.PageSize=30"
      )
      .then((response) => {
        const list = response.data.items;
        const toDoList = [];
        list.map((value) => {
          toDoList.push({
            id: uuidv4(),
            title: value.title,
            value: value.id,
            date: value.createdDate,
            employeeImagePath: value.employeeImagePath,
            supportRequestPriority: value.supportRequestPriority,
            supportRequestCategoryName: value.supportRequestCategoryName,
          });
          return list;
        });
        setToDo(toDoList);
      })
      .catch((error) => {
        toast.error(error.response.data.Message, {
          position: "top-right",
          autoClose: 1000,
        });
      })
      .finally(() => setIsLoadingTodo(true));
  };

  const GetInProgressList = () => {
    axios
      .get(
        "http://localhost:5010/api/SupportRequest/GetApprovedListByStatusTypeLoggedEmployeeId?SupportRequestStatusType=1&PageRequest.Page=0&PageRequest.PageSize=30"
      )
      .then((response) => {
        const list = response.data.items;
        const inProgressList = [];
        list.map((value) => {
          inProgressList.push({
            id: uuidv4(),
            title: value.title,
            value: value.id,
            date: value.createdDate,
            employeeImagePath: value.employeeImagePath,
            supportRequestPriority: value.supportRequestPriority,
            supportRequestCategoryName: value.supportRequestCategoryName,
          });
          return list;
        });
        setInProgress(inProgressList);
      })
      .catch((error) => {
        toast.error(error.response.data.Message, {
          position: "top-right",
          autoClose: 1000,
        });
      })
      .finally(() => setIsLoadingInProgress(true));
  };

  const GetDoneList = () => {
    axios
      .get(
        "http://localhost:5010/api/SupportRequest/GetApprovedListByStatusTypeLoggedEmployeeId?SupportRequestStatusType=2&PageRequest.Page=0&PageRequest.PageSize=30"
      )
      .then((response) => {
        const list = response.data.items;
        const doneList = [];
        list.map((value) => {
          doneList.push({
            id: uuidv4(),
            title: value.title,
            value: value.id,
            date: value.createdDate,
            employeeImagePath: value.employeeImagePath,
            supportRequestPriority: value.supportRequestPriority,
            supportRequestCategoryName: value.supportRequestCategoryName,
          });
          return list;
        });
        setDone(doneList);
      })
      .catch((error) => {
        toast.error(error.response.data.Message, {
          position: "top-right",
          autoClose: 1000,
        });
      })
      .finally(() => setIsLoadingDone(true));
  };

  useEffect(() => {
    GetToDoList();
    GetInProgressList();
    GetDoneList();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const onDragEnd = (result) => {
    console.log(result);
    console.log(result.source);
    console.log(result.destination);
    const { destination, source } = result;

    if (!destination) {
      return console.log("no destination");
    }

    if (source.droppableId !== destination.droppableId) {
      const sourceColIndex = data.findIndex((e) => e.id === source.droppableId);
      const destinationColIndex = data.findIndex(
        (e) => e.id === destination.droppableId
      );
      console.log(data[sourceColIndex].tasks[source.index]);
      console.log(data[sourceColIndex].tasks[source.index].value);
      var statusChancing = {
        id: data[sourceColIndex].tasks[source.index].value,
        supportRequestStatusType: destination.droppableId,
      };
      axios
        .put(
          "http://localhost:5010/api/SupportRequest/ChangeStatusType",
          statusChancing
        )
        .then((response) => {
          console.log("basarili");
        })
        .catch((error) => {
          toast.error(error.response.data.Message, {
            position: "top-right",
            autoClose: 1000,
          });
        });
      const sourceCol = data[sourceColIndex];
      const destinationCol = data[destinationColIndex];
      const sourceTask = [...sourceCol.tasks];
      const destinationTask = [...destinationCol.tasks];
      const [removed] = sourceTask.splice(source.index, 1);
      destinationTask.splice(destination.index, 0, removed);
      data[sourceColIndex].tasks = sourceTask;
      data[destinationColIndex].tasks = destinationTask;
      setData(data);
    } else {
      const sourceColIndex = data.findIndex((e) => e.id === source.droppableId);
      const sourceCol = data[sourceColIndex];
      const sourceTask = [...sourceCol.tasks];
      const [removed] = sourceTask.splice(source.index, 1);
      sourceTask.splice(destination.index, 0, removed);
      data[sourceColIndex].tasks = sourceTask;
      setData(data);
    }
  };

  if (isLoadingDone && isLoadingInProgress && isLoadingTodo) {
    kanbanList.push({
      id: 0,
      title: "ü§öNot Started",
      tasks: toDo,
    });
    kanbanList.push({
      id: 1,
      title: "üèÉ‚Äç‚ôÇÔ∏èIn progress",
      tasks: inProgress,
    });
    kanbanList.push({
      id: 2,
      title: "üôÜ‚Äç‚ôÄÔ∏èDone",
      tasks: done,
    });
  }

  console.log(toDo);
  console.log("------------");
  console.log(inProgress);
  console.log("------------");
  console.log(done);
  console.log("------------");
  console.log(kanbanList);
  console.log("------------");
  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <Grid container spacing={2}>
        {data.map((a) => (
          <Grid item xs={4}>
            <Droppable key={a.value} droppableId={a.id}>
              {(provided) => (
                <div ref={provided.innerRef} {...provided.droppableProps}>
                  <Stack
                    direction="row"
                    alignItems="center"
                    justifyContent="space-between"
                    mt={3}
                    mb={3}
                    p={2}
                  >
                    <Typography>{a.title}</Typography>
                    <IconButton
                      size="small"
                      component={Link}
                      to={`../support-request`}
                    >
                      <Iconify icon="material-symbols:add" />
                    </IconButton>
                  </Stack>
                  <Divider />
                  <div className="trello-section-content">
                    {a.tasks.map((b, index) => (
                      <Draggable draggableId={b.id} index={index} key={b.id}>
                        {(provided, snapshot) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                          >
                            <Card>
                              <Stack spacing={3}>
                                <Stack
                                  direction="row"
                                  alignItems="center"
                                  justifyContent="space-between"
                                >
                                  <Stack spacing={0.5} direction="row">
                                    <Chip
                                      label={
                                        (b.supportRequestPriority === 0 &&
                                          "low") ||
                                        (b.supportRequestPriority === 1 &&
                                          "medium") ||
                                        (b.supportRequestPriority === 2 &&
                                          "high") ||
                                        ""
                                      }
                                      size="small"
                                      color={
                                        (b.supportRequestPriority === 0 &&
                                          "success") ||
                                        (b.supportRequestPriority === 1 &&
                                          "warning") ||
                                        (b.supportRequestPriority === 2 &&
                                          "error") ||
                                        "info"
                                      }
                                      sx={{
                                        fontSize: "10px",
                                        fontWeight: "600",
                                      }}
                                    />
                                    <Chip
                                      label={b.supportRequestCategoryName}
                                      size="small"
                                      color="info"
                                      sx={{
                                        fontSize: "10px",
                                        fontWeight: "600",
                                      }}
                                    />
                                  </Stack>
                                  <Tooltip title="View">
                                    <IconButton
                                      size="small"
                                      component={Link}
                                      to={`../view-support-request/${b.id}`}
                                    >
                                      <Iconify icon="bi:three-dots" />
                                    </IconButton>
                                  </Tooltip>
                                </Stack>
                                <Stack
                                  direction="row"
                                  alignItems="center"
                                  justifyContent="space-between"
                                >
                                  <Link
                                    variant="body2"
                                    style={{
                                      textDecoration: "none",
                                    }}
                                  >
                                    {b.title}
                                  </Link>
                                  <Avatar
                                    alt="assignedUser"
                                    src={`http://localhost:5010/${b.employeeImagePath}`}
                                    sx={{ width: 30, height: 30 }}
                                  />
                                </Stack>
                                <Stack
                                  direction="row"
                                  alignItems="center"
                                  justifyContent="space-between"
                                >
                                  <Typography color="gray" variant="caption">
                                    <Iconify
                                      icon="mingcute:comment-line"
                                      sx={{ mr: 1 }}
                                    />
                                    0 comments
                                  </Typography>
                                  <Typography color="gray" variant="caption">
                                    {dateFormat(b.createdDate, "dS mmmm yyyy")}
                                  </Typography>
                                </Stack>
                              </Stack>
                            </Card>
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                </div>
              )}
            </Droppable>
          </Grid>
        ))}
      </Grid>
    </DragDropContext>
  );
};

export default CodeBlock;
