import { Icon } from "@iconify/react";
import { Container, Typography } from "@mui/material";
import { useEffect, useRef, useState } from "react";
import { Nav, Navbar, Offcanvas } from "react-bootstrap";

export default function Header() {
  const [navBackground, setNavBackground] = useState(false);
  const [navColor, setNavColor] = useState(false);
  const navRef = useRef();
  navRef.current = navBackground;
  useEffect(() => {
    const handleScroll = () => {
      const show = window.scrollY > 50;
      if (navRef.current !== show) {
        setNavBackground(show);
        setNavColor(show);
      }
    };
    document.addEventListener("scroll", handleScroll);
    return () => {
      document.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Navbar
      expand={false}
      style={{
        backgroundColor: navBackground ? "#ededed" : "transparent",
        height: "10vh",
      }}
    >
      <Container
        disableGutters
        maxWidth="lg"
        sx={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-between",
        }}
      >
        <Navbar.Brand href="#home" style={{ mr: 10 }}>
          <Typography
            variant="h5"
            color={navColor ? "black" : "white"}
            fontWeight="700"
          >
            Ã–ZNUR YILMAZ
          </Typography>
        </Navbar.Brand>
        <Navbar.Toggle
          aria-controls="offcanvasNavbar-expand-sm"
          style={{ backgroundColor: "transparent", border: 0 }}
        >
          <Icon
            icon="akar-icons:three-line-horizontal"
            width={25}
            height={25}
            color={"rgb(120 120 120)"}
          />
        </Navbar.Toggle>
        <Navbar.Offcanvas
          placement="end"
          style={{ backgroundColor: "rgba(22,22,22,0.78)" }}
        >
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>Offcanvas</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <Nav className="me-auto">
              <Nav.Link href="#home" style={{ color: "white" }}>
                Home
              </Nav.Link>
              <Nav.Link href="#features" style={{ color: "white" }}>
                Features
              </Nav.Link>
              <Nav.Link href="#pricing" style={{ color: "white" }}>
                Pricing
              </Nav.Link>
            </Nav>
          </Offcanvas.Body>
        </Navbar.Offcanvas>
      </Container>
    </Navbar>
  );
}
