import HeroSlider, { Slide } from "hero-slider";

export default function Slider() {
  return (
    <HeroSlider
      slidingAnimation="top_to_bottom"
      orientation="vertical"
      initialSlide={1}
      onBeforeChange={(previousSlide, nextSlide) =>
        console.log("onBeforeChange", previousSlide, nextSlide)
      }
      onChange={(nextSlide) => console.log("onChange", nextSlide)}
      onAfterChange={(nextSlide) => console.log("onAfterChange", nextSlide)}
      style={{
        backgroundColor: "#000",
      }}
      settings={{
        slidingDuration: 400,
        slidingDelay: 100,
        shouldAutoplay: true,
        shouldDisplayButtons: false,
        autoplayDuration: 8000,
        height: "100vh",
      }}
    >
      <Slide
        shouldRenderMask
        navDescription="Kyoto - Japan"
        background={{
          backgroundImage: "./img/background.jpg",
        }}
      />

      <Slide
        navDescription="Tenryu-ji Temple - KyÅto-shi - Japan"
        background={{
          backgroundImage: "https://i.imgur.com/xw5Abku.jpg",
        }}
      />

      <Slide
        navDescription="Hakone - Japan"
        background={{
          backgroundImage:
            "https://images.unsplash.com/photo-1607799279861-4dd421887fb3?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
        }}
      />
    </HeroSlider>
  );
}
