import React from "react";
// JSX
import HeroSlider, { Slide } from "hero-slider";
import "./css/style.css";
import {
  Button,
  Container,
  Typography,
  Stack,
  useMediaQuery,
  CardMedia,
  Card,
} from "@mui/material";
import TransparentNavbar from "../../layout/Navbar/TransparentNavbar";
import { Link } from "react-router-dom";
import { styled } from "@mui/material/styles";
import config from "../../config";
import { useTranslation } from "react-i18next";

const PUBLIC_IMAGE_URL = config.imageBaseUrl;
const BackgroundContainer = styled(Container)({
  // Your styles here
  backgroundImage: `url(${PUBLIC_IMAGE_URL}/2023/12/dark-bg-scaled.jpg)`,
  // Add more styles as needed
});

export default function Slider() {
  const isMobile = useMediaQuery("(max-width: 1000px)");
  const { i18n, t } = useTranslation();
  const currentLanguage = i18n.language;

  return (
    <Container maxWidth="fluid" disableGutters sx={{ position: "relative" }}>
      <BackgroundContainer maxWidth="fluid">
        <Stack mb={(isMobile === true && 10) || 25}>
          <TransparentNavbar />
        </Stack>
        <HeroSlider
          orientation="horizontal"
          initialSlide={1}
          onBeforeChange={(previousSlide, nextSlide) =>
            console.log("onBeforeChange", previousSlide, nextSlide)
          }
          onChange={(nextSlide) => console.log("onChange", nextSlide)}
          onAfterChange={(nextSlide) => console.log("onAfterChange", nextSlide)}
          autoplay
          controller={{ slidingDuration: 500, slidingDelay: 100 }}
        >
          <Slide navDescription={t("StoriesThatFrostTheTime")}>
            <Container>
              <Stack
                direction={(isMobile === true && "column") || "row"}
                justifyContent="center"
                alignItems="center"
                spacing={5}
              >
                <Stack
                  justifyContent="center"
                  alignItems="center"
                  position={(isMobile === true && "absolute") || ""}
                  spacing={1}
                >
                  <Typography
                    fontSize="28px"
                    fontWeight="bolder"
                    color="white"
                    textAlign="center"
                    maxWidth={400}
                    lineHeight={1}
                    textTransform="uppercase"
                  >
                    {t("StoriesThatFrostTheTime")}
                  </Typography>
                  <Typography
                    fontSize="20px"
                    fontWeight="normal"
                    color="white"
                    textTransform="uppercase"
                  >
                    {t("AnEpicExperience")}
                  </Typography>
                  <Typography
                    fontSize="14px"
                    fontWeight="400"
                    color="white"
                    textAlign="center"
                  >
                    {t("AnEpicExperienceDescription")}
                  </Typography>
                  <Button
                    variant="contained"
                    component={Link}
                    to={
                      (currentLanguage === "tr" && "../hakkimizda") ||
                      "../en/about-us"
                    }
                    sx={{
                      backgroundColor: "#446084!important",
                      borderRadius: 0,
                      mt: 2,
                      width: 120,
                      height: 35,
                      textTransform: "uppercase",
                    }}
                  >
                    {t("AboutUs")}
                  </Button>
                </Stack>
                <Card
                  sx={{ maxWidth: 500, bgcolor: "transparent" }}
                  elevation={0}
                >
                  <CardMedia
                    component="img"
                    width={(isMobile === true && "370px") || "500px"}
                    src={`${PUBLIC_IMAGE_URL}/2023/12/kacka-slider.png`}
                    alt="kacka,hotda,frostline games,frostline"
                  />
                </Card>
              </Stack>
            </Container>
          </Slide>
          <Slide navDescription={t("WordBearsTheSealOfTruth")}>
            <Container>
              <Stack
                direction={(isMobile === true && "column") || "row"}
                justifyContent="center"
                alignItems="center"
                spacing={5}
              >
                <Stack
                  justifyContent="center"
                  alignItems="center"
                  position={(isMobile === true && "absolute") || ""}
                  spacing={1}
                >
                  <Typography
                    fontSize="28px"
                    fontWeight="bolder"
                    color="white"
                    textAlign="center"
                    maxWidth={400}
                    lineHeight={1}
                    textTransform="uppercase"
                  >
                    {t("WordBearsTheSealOfTruth")}
                  </Typography>
                  <Typography
                    fontSize="20px"
                    fontWeight="normal"
                    color="white"
                    textTransform="uppercase"
                  >
                    {t("HeroesOfTheDarkAges")}
                  </Typography>
                  <Typography
                    fontSize="14px"
                    fontWeight="400"
                    color="white"
                    textAlign="center"
                  >
                    {t("HeroesOfTheDarkAgesDescription")}
                  </Typography>
                  <Stack
                    direction="row"
                    justifyContent="center"
                    alignItems="center"
                  >
                    <Button
                      variant="contained"
                      component={Link}
                      to={
                        (currentLanguage === "tr" && "../hakkimizda") ||
                        "../en/about-us"
                      }
                      sx={{
                        backgroundColor: "#446084!important",
                        borderRadius: 0,
                        mt: 2,
                        mr: 2,
                        width: 120,
                        height: 35,
                        textTransform: "uppercase",
                      }}
                    >
                      {t("AboutUs")}
                    </Button>
                    <Button
                      variant="outlined"
                      component={Link}
                      to={
                        (currentLanguage === "tr" && "../oyunlarimiz") ||
                        "../en/our-games"
                      }
                      sx={{
                        borderColor: "white!important",
                        color: "white",
                        borderRadius: 0,
                        mt: 2,
                        width: 120,
                        height: 35,
                        textTransform: "uppercase",
                      }}
                    >
                      {t("OurGames")}
                    </Button>
                  </Stack>
                </Stack>
                <Card
                  sx={{ maxWidth: 500, bgcolor: "transparent" }}
                  elevation={0}
                >
                  <CardMedia
                    component="img"
                    width={(isMobile === true && "370px") || "500px"}
                    src={`${PUBLIC_IMAGE_URL}/2023/12/kacka3.png`}
                    alt="kacka,hotda,frostline games,frostline"
                  />
                </Card>
              </Stack>
            </Container>
          </Slide>
          <Slide navDescription={t("ForUsFromUs")}>
            <Container>
              <Stack
                direction={(isMobile === true && "column") || "row"}
                justifyContent="center"
                alignItems="center"
                spacing={5}
              >
                <Stack
                  justifyContent="center"
                  alignItems="center"
                  position={(isMobile === true && "absolute") || ""}
                  spacing={1}
                >
                  <Typography
                    fontSize="28px"
                    fontWeight="bolder"
                    color="white"
                    textAlign="center"
                    maxWidth={400}
                    lineHeight={1}
                  >
                    {t("ForUsFromUs")}
                  </Typography>
                  <Typography fontSize="20px" fontWeight="normal" color="white">
                    {t("GamesForGamers")}
                  </Typography>
                  <Typography
                    fontSize="14px"
                    fontWeight="400"
                    color="white"
                    textAlign="center"
                  >
                    {t("GamesForGamersDescription")}
                  </Typography>
                  <Stack
                    direction="row"
                    justifyContent="center"
                    alignItems="center"
                  >
                    <Button
                      variant="contained"
                      component={Link}
                      to={
                        (currentLanguage === "tr" && "../kariyer") ||
                        "../en/career"
                      }
                      sx={{
                        backgroundColor: "#446084!important",
                        borderRadius: 0,
                        mt: 2,
                        mr: 2,
                        width: 120,
                        height: 35,
                        textTransform: "uppercase",
                      }}
                    >
                      {t("Career")}
                    </Button>
                    <Button
                      variant="outlined"
                      component={Link}
                      to={
                        (currentLanguage === "tr" && "../iletisim") ||
                        "../en/contact-us"
                      }
                      sx={{
                        borderColor: "white!important",
                        color: "white",
                        borderRadius: 0,
                        mt: 2,
                        width: 120,
                        height: 35,
                        textTransform: "uppercase",
                      }}
                    >
                      {t("Contact")}
                    </Button>
                  </Stack>
                </Stack>
                <Card
                  sx={{ maxWidth: 500, bgcolor: "transparent" }}
                  elevation={0}
                >
                  <CardMedia
                    component="img"
                    width={(isMobile === true && "370px") || "500px"}
                    src={`${PUBLIC_IMAGE_URL}/2023/12/slide-circle.png`}
                    alt="kacka,hotda,frostline games,frostline"
                  />
                </Card>
              </Stack>
            </Container>
          </Slide>
