import { Icon } from "@iconify/react";
import { Box, IconButton } from "@mui/joy";
import { Container, Typography } from "@mui/material";
import { useEffect, useRef, useState } from "react";
import { Nav, Navbar, Offcanvas } from "react-bootstrap";
import { Link } from "react-router-dom";

export default function Header() {
  const [navBackground, setNavBackground] = useState(false);
  const [navColor, setNavColor] = useState(false);
  const navRef = useRef();
  navRef.current = navBackground;
  useEffect(() => {
    const handleScroll = () => {
      const show = window.scrollY > 50;
      if (navRef.current !== show) {
        setNavBackground(show);
        setNavColor(show);
      }
    };
    document.addEventListener("scroll", handleScroll);
    return () => {
      document.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <Navbar
      expand={false}
      style={{
        backgroundColor: "transparent",
        height: "10vh",
      }}
    >
      <Container
        disableGutters
        maxWidth="lg"
        sx={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-between",
        }}
      >
        <Navbar.Brand href="#home" style={{ mr: 10 }}>
          <Typography variant="h5" color="white" fontWeight="700">
            Ã–ZNUR YILMAZ
          </Typography>
        </Navbar.Brand>
        <Navbar.Toggle
          aria-controls="offcanvasNavbar-expand-sm"
          style={{ backgroundColor: "transparent", border: 0 }}
        >
          <Box
            sx={{
              display: "flex",
              gap: 1,
              mt: 2,
              "& > button": { borderRadius: "2rem" },
            }}
          >
            <IconButton
              size="sm"
              variant="plain"
              component={Link}
              target="_blank"
              to="https://www.linkedin.com/in/oznuryilmazz/"
            >
              <Icon icon="mdi:linkedin" color="white" width="24" height="24" />
            </IconButton>
            <IconButton
              size="sm"
              variant="plain"
              component={Link}
              target="_blank"
              to={"https://github.com/oznuryilmazz"}
            >
              <Icon icon="mdi:github" color="white" width="24" height="24" />
            </IconButton>
            <IconButton
              size="sm"
              variant="plain"
              component={Link}
              target="_blank"
              to="https://www.instagram.com/ozywebdesigner/"
            >
              <Icon icon="mdi:instagram" color="white" width="24" height="24" />
            </IconButton>
          </Box>
        </Navbar.Toggle>
      </Container>
    </Navbar>
  );
}
